package com.example.demo.Controller;import com.example.demo.Model.ClassJoin;import com.example.demo.Model.Role;import com.example.demo.Model.User;import com.example.demo.Service.ClassJoinService;import com.example.demo.Service.RoleService;import com.example.demo.Service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ModelAttribute;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.servlet.ModelAndView;import java.text.DateFormat;import java.text.SimpleDateFormat;import java.time.LocalDateTime;import java.time.format.DateTimeFormatter;import java.util.Date;@RequestMapping("/admin")@Controllerpublic class UserController extends AdminBaseController {    private final  String TERM = "Users ";    @RequestMapping(value = "/users", method = RequestMethod.GET)    public ModelAndView ShowUser(){        ModelAndView modelAndView =new ModelAndView("/User/list");        modelAndView.addObject("title",TITLE_ADD);        modelAndView.addObject("term",TERM);        modelAndView.addObject("userInfo",new User());        return modelAndView;    }}